    def return_checked_color(self): # this functions just checks which radio button got selected
        if self.rdb_blue.isChecked()==True:
            print('blue is clicked')
            return 'blue'
            # return self.rdb_blue.text()
        elif self.rdb_orange.isChecked()==True:
            print('orange is clicked')
            return 'orange'
            # return self.rdb_orange.text()
        elif self.rdb_yellow.isChecked()==True:
            print('yellow is clicked')
            return 'yellow'
            # return self.rdb_yellow.text()
        
