    def set_background(self,color):
        print ('set_background: function called.')
        if color == 'blue':
            print('Bule color is setted in the background')
            brush = QtGui.QBrush(QtGui.QColor(170, 30, 255))
            brush.setStyle(QtCore.Qt.SolidPattern)
            self.palette.setBrush(QtGui.QPalette.Active, QtGui.QPalette.Window, brush)
            MainWindow.setPalette(self.palette)
        elif color == 'yellow':
            brush = QtGui.QBrush(QtGui.QColor(255, 255, 0))
            brush.setStyle(QtCore.Qt.SolidPattern)
            self.palette.setBrush(QtGui.QPalette.Active, QtGui.QPalette.Window, brush)
            MainWindow.setPalette(self.palette)
            # self.centralwidget = QtWidgets.QWidget(MainWindow)
        elif color == 'orange':
            brush = QtGui.QBrush(QtGui.QColor(0, 170, 0))
            brush.setStyle(QtCore.Qt.SolidPattern)
            self.palette.setBrush(QtGui.QPalette.Active, QtGui.QPalette.Window, brush)
            MainWindow.setPalette(self.palette)
        elif color == 'nocolor':
            print('No color is setted in the background')
            brush = QtGui.QBrush(QtGui.QColor(255, 255, 255))
            brush.setStyle(QtCore.Qt.SolidPattern)
            self.palette.setBrush(QtGui.QPalette.Active, QtGui.QPalette.Window, brush)
